mapper.update:
        update product
        set product_price = #{product_price}, product_name = #{product_name}

	        , product_image = #{product_image}

        where product_id = #{product_id}

mapper.select:
  	select * from `order`, branch 
  	where `order`.order_sender = branch.branch_id
  	and `order`.order_type = 0


 			and branch.branch_name like concat('%',#{keyword},'%')


 			and date(`order`.order_date) >= #{startDate1}


 			and date(`order`.order_delivery) >= #{startDate2}


 			and date(`order`.order_date) <= #{endDate1}


 			and date(`order`.order_delivery) <= #{endDate2}


 			and `order`.order_status = #{status}


		ORDER BY `order`.order_date DESC
 		LIMIT #{start}, #{pageSize}

     SELECT COUNT(*)
     FROM `order`, branch
     WHERE `order`.order_sender = branch.branch_id
     AND order_type = 0


			and branch.branch_name like concat('%',#{keyword},'%')


			and date(`order`.order_date) >= #{startDate1}


			and date(`order`.order_delivery) >= #{startDate2}


			and date(`order`.order_date) <= #{endDate1}


			and date(`order`.order_delivery) <= #{endDate2}


			and `order`.order_status = #{status}

update.foreach:
update stock
set stock_remain = stock_remain - #{item.orderDetail_amount}
where stock_productid = #{item.orderDetail_productid}
and stock_type = 1

mapper.select:
SELECT * 
FROM `orderdetail`, `product`, `stock` 
WHERE orderDetail_orderid = #{orderDetail_orderid}
and orderdetail.orderDetail_productid = product.product_id
and stock.stock_productid = orderdetail.orderDetail_productid
and stock.stock_type = 1;

select SUM(sale.sale_sum) as sale_sum, sale.sale_branchid, DATE(sale.sale_date) as sale_date, branch.branch_name, branch.branch_id from sale join branch on branch.branch_id = sale.sale_branchid
and branch.branch_name like concat('%',#{keyword},'%')
and date(sale.sale_date) >= #{startDate}
and date(sale.sale_date) <= #{endDate}
group by DATE(sale.sale_date), sale.sale_branchid, branch.branch_name, branch.branch_id

select SUM(sale.sale_sum) AS sale_sum, sale.sale_branchid, DATE(sale.sale_date) as sale_date, branch.branch_name, branch.branch_id from sale join branch on branch.branch_id = sale.sale_branchid
and branch.branch_name like concat('%',#{keyword},'%')
and date(sale.sale_date) >= #{startDate}
and date(sale.sale_date) <= #{endDate}
group by DATE(sale.sale_date), sale.sale_branchid, branch.branch_name, branch.branch_id ORDER BY DATE(sale.sale_date) DESC LIMIT #{start}, #{pageSize}

        select count(*)
        from (
	        select
				date(sale_date),
	            sale_branchid
			from sale, branch
			where branch.branch_id = sale.sale_branchid

	   			and branch.branch_name like concat('%',#{keyword},'%')


	   			and date(sale.sale_date) >= #{startDate}


	   			and date(sale.sale_date) <= #{endDate}

	        group by DATE(sale_date), 
			    sale_branchid
		    ) AS subquery

SELECT * FROM branch WHERE 1=1
AND branch_name LIKE CONCAT('%', #{branch_name}, '%')
AND branch_owner LIKE CONCAT('%', #{branch_owner}, '%')
AND DATE(branch_regdate) >= #{startDate}
AND DATE(branch_regdate) <= #{endDate}
AND branch_region LIKE CONCAT('%', #{branch_region}, '%')

select * from hr


	 hr_name like concat('%',#{staff_search},'%')


	 and hr_grade = #{grade}


	 and hr_joindate between #{start} and #{end}


	 and hr_authority = #{auth}


	 and hr_del = 0
